	position: absolute;
	width: 100%;
	flex-direction: row;
}

.tc-slider-pointerdown-eventcatcher:not(.tc-main-slider) {
	position: relative;
}

.tc-slider, .tc-slider-pointermove-eventcatcher, .tc-slider-pointermove {
	touch-action: none;
	user-select: none;
}

.tc-slider-pointermove-eventcatcher-wrapper.tc-main-slider.tc-resizer-active {
